import React, { useState } from "react";

const WorkoutPlanner = () => {
  const [goal, setGoal] = useState("");
  const [plan, setPlan] = useState(null);

  const generatePlan = async () => {
    const res = await fetch("http://localhost:5000/api/workout/plan", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${JSON.parse(localStorage.getItem("user")).token}`,
      },
      body: JSON.stringify({ goal }),
    });
    const data = await res.json();
    setPlan(data.plan);
  };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Lên kế hoạch tập luyện</h2>
      <input
        type="text"
        placeholder="Ví dụ: giảm 5kg, tăng 2kg cơ..."
        value={goal}
        onChange={(e) => setGoal(e.target.value)}
        className="w-full mb-3 px-4 py-2 border rounded"
      />
      <button onClick={generatePlan} className="bg-green-600 text-white px-4 py-2 rounded">
        Tạo kế hoạch
      </button>
      {plan && (
        <div className="mt-4 bg-gray-100 p-4 rounded whitespace-pre-wrap">
          {plan}
        </div>
      )}
    </div>
  );
};

export default WorkoutPlanner;
