import React, { useState } from 'react';
import api from '../services/api';
import { toast } from 'react-toastify';

export default function CalorieEstimator() {
  const [description, setDescription] = useState('');
  const [calories, setCalories] = useState(null);

  const handleEstimate = async () => {
    try {
      const res = await api.post('/calories/estimate', { description });
      setCalories(res.data.calories);
    } catch (err) {
      toast.error('Failed to estimate calories');
    }
  };

  return (
    <div className="container mt-4">
      <h2>Estimate Calories from Description</h2>
      <textarea
        className="form-control my-3"
        rows="3"
        placeholder="E.g. 1 bowl of pho, 1 can of coke"
        value={description}
        onChange={e => setDescription(e.target.value)}
      />
      <button className="btn btn-info" onClick={handleEstimate}>Estimate</button>
      {calories !== null && (
        <div className="alert alert-success mt-3">
          Estimated Calories: {calories} kcal
        </div>
      )}
    </div>
  );
}
