import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { loginUser } from '../services/api';

const Login = () => {
  const [identifier, setIdentifier] = useState(''); // username hoặc email
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    // Validation phía client
    if (!identifier.trim()) {
      setError('Please enter your username or email');
      return;
    }
    if (password.length < 6) {
      setError('Password must be at least 6 characters');
      return;
    }

    setLoading(true);
    try {
      const res = await loginUser({ identifier, password });

      // Nếu login thành công
      if (res.data?.token && res.data?.user) {
        const user = res.data.user;

        localStorage.setItem('token', res.data.token);
        localStorage.setItem('user', JSON.stringify(user));
        localStorage.setItem('userId', user._id);       // Lưu userId
        localStorage.setItem('username', user.username); // Lưu username

        navigate('/');
      } else {
        setError(res.data?.error || res.data?.message || 'Invalid username/email or password');
      }
    } catch (err) {
      if (err.response) {
        const status = err.response.status;
        if (status === 401) {
          setError('Incorrect password');
        } else if (status === 404) {
          setError('User not found');
        } else {
          setError(err.response.data?.message || 'Login failed');
        }
      } else if (err.request) {
        setError('Unable to connect to the server');
      } else {
        setError('An unexpected error occurred');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Username or Email"
          value={identifier}
          onChange={(e) => setIdentifier(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        {error && <p className="error">{error}</p>}
        <button type="submit" disabled={loading}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
      </form>
    </div>
  );
};

export default Login;
