import React, { useState } from 'react';
import { generateMeals, estimateCaloriesFromText, estimateCaloriesFromImage } from '../services/api';
import MealCard from '../components/MealCard';

const Home = () => {
  const [calorieTarget, setCalorieTarget] = useState('');
  const [numMeals, setNumMeals] = useState('3');
  const [meals, setMeals] = useState([]);
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);
  const [estimatedCalories, setEstimatedCalories] = useState(null);

  const handleGenerateMeals = async () => {
    const res = await generateMeals({ calorieTarget, numMeals, userId: 1 });
    setMeals(res.data.meals);
  };

  const handleEstimateText = async () => {
    const res = await estimateCaloriesFromText({ description });
    setEstimatedCalories(res.data.calories);
  };

  const handleEstimateImage = async () => {
    const formData = new FormData();
    formData.append('image', image);
    const res = await estimateCaloriesFromImage(formData);
    setEstimatedCalories(res.data.calories);
  };

  return (
    <div className="p-4 grid grid-cols-2 gap-4">
      <div className="space-y-4">
        <h2 className="text-xl font-bold">Tạo thực đơn AI</h2>
        <input placeholder="Calories" value={calorieTarget} onChange={(e) => setCalorieTarget(e.target.value)} className="border p-2 w-full" />
        <input placeholder="Số bữa" value={numMeals} onChange={(e) => setNumMeals(e.target.value)} className="border p-2 w-full" />
        <button onClick={handleGenerateMeals} className="bg-green-500 text-white px-4 py-2 rounded">Tạo</button>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
          {meals.map((meal, idx) => <MealCard key={idx} meal={meal} />)}
        </div>
      </div>
      <div className="space-y-4">
        <h2 className="text-xl font-bold">Ước lượng calo</h2>
        <input placeholder="Mô tả món ăn" value={description} onChange={(e) => setDescription(e.target.value)} className="border p-2 w-full" />
        <button onClick={handleEstimateText} className="bg-blue-500 text-white px-4 py-2 rounded">Từ mô tả</button>

        <input type="file" onChange={(e) => setImage(e.target.files[0])} className="block" />
        <button onClick={handleEstimateImage} className="bg-purple-500 text-white px-4 py-2 rounded">Từ ảnh</button>

        {estimatedCalories !== null && (
          <div className="text-lg font-semibold">Ước lượng: {estimatedCalories} kcal</div>
        )}
      </div>
    </div>
  );
};

export default Home;
