import { useState } from 'react';
import { generateMeals, estimateCaloriesFromText, estimateCaloriesFromImage } from '../services/api';
import '../styles/Home.scss';

const Home = () => {
  const [calorieTarget, setCalorieTarget] = useState('');
  const [numMeals, setNumMeals] = useState('3');
  const [meals, setMeals] = useState([]);
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);
  const [estimatedCalories, setEstimatedCalories] = useState(null);

  const handleGenerateMeals = async () => {
    const res = await generateMeals({ calorieTarget, numMeals, userId: 1 });
    setMeals(res.data.meals);
  };

  const handleEstimateText = async () => {
    const res = await estimateCaloriesFromText({ description });
    setEstimatedCalories(res.data.calories);
  };

  const handleEstimateImage = async () => {
    const formData = new FormData();
    formData.append('image', image);
    const res = await estimateCaloriesFromImage(formData);
    setEstimatedCalories(res.data.calories);
  };

  return (
    <div className="home-container">
      <div className="meal-generator">
        <h2>Put your diet on <span>Autopilot</span>.</h2>
        <p className="subtitle">Create your meal plan right here in seconds</p>

        <div className="inputs">
          <label>I want to eat</label>
          <input
            type="number"
            placeholder="Calories"
            value={calorieTarget}
            onChange={(e) => setCalorieTarget(e.target.value)}
          />

          <label>in</label>
          <select value={numMeals} onChange={(e) => setNumMeals(e.target.value)}>
            <option value="1">1 meal</option>
            <option value="2">2 meals</option>
            <option value="3">3 meals</option>
            <option value="4">4 meals</option>
          </select>

          <button className="generate-button" onClick={handleGenerateMeals}>Generate</button>
        </div>

        <div className="meal-plan">
          {meals.map((meal, idx) => (
            <div key={idx} className="meal-section">
              <h3>{meal.type} - {meal.totalCalories} Calories</h3>
              <ul>
                {meal.items.map((item, index) => (
                  <li key={index} className="meal-item">
                    <img src={item.image} alt={item.name} />
                    <div>
                      <p className="item-name">{item.name}</p>
                      <p className="item-serving">{item.servings} serving(s)</p>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>

      <div className="calorie-estimator">
        <h2>Ước lượng calo</h2>
        <input
          placeholder="Mô tả món ăn"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <button onClick={handleEstimateText}>Từ mô tả</button>

        <input type="file" onChange={(e) => setImage(e.target.files[0])} />
        <button onClick={handleEstimateImage}>Từ ảnh</button>

        {estimatedCalories !== null && (
          <div className="result">Ước lượng: {estimatedCalories} kcal</div>
        )}
      </div>
    </div>
  );
};

export default Home;
