import React, { useState } from 'react';

export default function GoalPlanner() {
  const [goal, setGoal] = useState('');
  const [plan, setPlan] = useState('');

  const generatePlan = () => {
    let message = '';
    if (goal === 'lose') {
      message = 'Suggested Plan: Cardio 30 mins/day, reduce 500 kcal/day from meals.';
    } else if (goal === 'gain') {
      message = 'Suggested Plan: Strength training 4x/week, add 500 kcal/day in meals.';
    } else if (goal === 'muscle') {
      message = 'Suggested Plan: Weightlifting 5x/week, eat high-protein diet.';
    } else {
      message = 'Please select a goal';
    }
    setPlan(message);
  };

  return (
    <div className="container mt-4">
      <h2>Fitness Goal Planner</h2>
      <select className="form-select my-3" value={goal} onChange={e => setGoal(e.target.value)}>
        <option value="">-- Select Goal --</option>
        <option value="lose">Lose Weight</option>
        <option value="gain">Gain Weight</option>
        <option value="muscle">Build Muscle</option>
      </select>
      <button className="btn btn-primary" onClick={generatePlan}>Generate Plan</button>
      {plan && <div className="alert alert-info mt-3">{plan}</div>}
    </div>
  );
}
