import React, { useState, useEffect, useContext } from "react";
import { generateWorkoutPlan } from "../services/api";
import AuthContext from "../context/AuthContext"; // Giả sử bạn có AuthContext
import "../styles/WorkoutPlan.scss";

const WorkoutPlan = () => {
  const { user } = useContext(AuthContext); // Lấy thông tin user (id, age, weight, height, gender)
  const [goalType, setGoalType] = useState("lose");
  const [amount, setAmount] = useState("");
  const [unit, setUnit] = useState("kg");
  const [workoutPlan, setWorkoutPlan] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // Fetch plans hiện có khi load component
  useEffect(() => {
    const fetchPlans = async () => {
      try {
        const response = await generateWorkoutPlan({
          userId: user.id,
          goalType: "fetch", // Endpoint có thể cần flag để fetch
        });
        if (response.data?.workouts?.length) {
          setWorkoutPlan(response.data.workouts);
        }
      } catch (err) {
        console.error("Lỗi fetch plans:", err);
      }
    };
    if (user?.id) fetchPlans(); // Chỉ fetch nếu user đã login
  }, [user]);

  // Handle generate workout plan
  const handleGenerateWorkout = async () => {
    if (!amount || Number(amount) <= 0) {
      setError("Vui lòng nhập số lượng mục tiêu hợp lệ");
      return;
    }

    setLoading(true);
    setError("");

    try {
      const response = await generateWorkoutPlan({
        userId: user.id,
        goalType,
        amount: Number(amount),
        unit,
        age: user.age,
        weight: user.weight,
        height: user.height,
        gender: user.gender,
        duration: "1 tháng", // Default, có thể thêm input cho duration
      });

      if (response.data?.workouts?.length) {
        setWorkoutPlan(response.data.workouts);
      } else {
        setWorkoutPlan([]);
        setError("Không tìm thấy kế hoạch tập luyện phù hợp");
      }
    } catch (err) {
      setError("Đã xảy ra lỗi khi gọi API");
      setWorkoutPlan([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="workout-container">
      <h2 className="title">💪 Lên kế hoạch tập luyện</h2>

      <div className="form-card">
        <div className="form-row">
          <select
            value={goalType}
            onChange={(e) => setGoalType(e.target.value)}
            className="goal-select"
          >
            <option value="lose">Giảm cân</option>
            <option value="gain">Tăng cân</option>
            <option value="muscle">Tăng cơ</option>
          </select>

          <input
            type="number"
            placeholder="Số lượng (vd: 5)"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            min="1"
            className="amount-input"
          />

          <select
            value={unit}
            onChange={(e) => setUnit(e.target.value)}
            className="unit-select"
          >
            <option value="kg">kg</option>
            <option value="%">%</option>
          </select>
        </div>

        {error && <div className="error-text">{error}</div>}

        <button
          className="generate-button"
          onClick={handleGenerateWorkout}
          disabled={loading || !user?.id} // Disable nếu chưa login
        >
          {loading ? "Đang tạo..." : "Tạo kế hoạch tập luyện"}
        </button>
      </div>

      <div className="workout-list">
        {workoutPlan.length > 0 ? (
          workoutPlan.map((workout, index) => (
            <div className="workout-card" key={index}>
              {workout.image && (
                <img
                  src={workout.image}
                  alt={workout.name}
                  className="workout-image"
                />
              )}
              <div className="workout-info">
                <h3 className="workout-name">{workout.name}</h3>
                <p className="workout-description">{workout.description}</p>
                {workout.duration && (
                  <p className="workout-duration">⏱ {workout.duration}</p>
                )}
                {workout.weeklyPlan && ( // Hiển thị weekly plan từ Ollama nếu có
                  <pre className="weekly-plan">
                    {JSON.stringify(workout.weeklyPlan, null, 2)}
                  </pre>
                )}
              </div>
            </div>
          ))
        ) : (
          !loading && <p className="no-plan">Chưa có kế hoạch nào được tạo.</p>
        )}
      </div>
    </div>
  );
};

export default WorkoutPlan;
