import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getHistory } from '../services/api';
import HistoryItem from '../components/HistoryItem';
import '../styles/History.scss';

const History = () => {
  const [history, setHistory] = useState({ meals: [], workouts: [] });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const navigate = useNavigate();

  // L·∫•y userId t·ª´ localStorage v√† √©p ki·ªÉu an to√†n
  const storedId = localStorage.getItem('userId');
  const userId = Number(storedId) || null;

  useEffect(() => {
    const fetchHistory = async () => {
      if (!userId) {
        setError('‚ö† B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ xem l·ªãch s·ª≠.');
        setLoading(false);
        return;
      }

      try {
        const res = await getHistory(userId);

        // ƒê·∫£m b·∫£o d·ªØ li·ªáu h·ª£p l·ªá
        const meals = Array.isArray(res?.data?.meals) ? res.data.meals : [];
        const workouts = Array.isArray(res?.data?.workouts) ? res.data.workouts : [];

        setHistory({ meals, workouts });
      } catch (err) {
        console.error('L·ªói khi l·∫•y l·ªãch s·ª≠:', err);
        setError('Kh√¥ng th·ªÉ t·∫£i d·ªØ li·ªáu l·ªãch s·ª≠. Vui l√≤ng th·ª≠ l·∫°i.');
      } finally {
        setLoading(false);
      }
    };

    fetchHistory();
  }, [userId]);

  // Tr·∫°ng th√°i loading
  if (loading) {
    return <div className="history-container">‚è≥ ƒêang t·∫£i d·ªØ li·ªáu...</div>;
  }

  // Tr·∫°ng th√°i l·ªói
  if (error) {
    return (
      <div className="history-container error-message">
        {error}
        {!userId && (
          <button
            className="login-btn"
            onClick={() => navigate('/login')}
            style={{ marginTop: '10px' }}
          >
            ƒêƒÉng nh·∫≠p ngay
          </button>
        )}
      </div>
    );
  }

  return (
    <div className="history-container">
      <h2 className="history-title">üìú L·ªãch s·ª≠ ho·∫°t ƒë·ªông c·ªßa b·∫°n</h2>

      {/* L·ªãch s·ª≠ b·ªØa ƒÉn */}
      <div className="history-section">
        <h3 className="section-title">üç± L·ªãch s·ª≠ b·ªØa ƒÉn</h3>
        {history.meals.length > 0 ? (
          history.meals.map((item, idx) => (
            <HistoryItem
              key={item.id || `meal-${idx}`}
              item={item}
              type="meal"
            />
          ))
        ) : (
          <p className="no-history">Kh√¥ng c√≥ d·ªØ li·ªáu b·ªØa ƒÉn.</p>
        )}
      </div>

      {/* L·ªãch s·ª≠ t·∫≠p luy·ªán */}
      <div className="history-section">
        <h3 className="section-title">üí™ L·ªãch s·ª≠ t·∫≠p luy·ªán</h3>
        {history.workouts.length > 0 ? (
          history.workouts.map((item, idx) => (
            <HistoryItem
              key={item.id || `workout-${idx}`}
              item={item}
              type="workout"
            />
          ))
        ) : (
          <p className="no-history">Kh√¥ng c√≥ d·ªØ li·ªáu t·∫≠p luy·ªán.</p>
        )}
      </div>
    </div>
  );
};

export default History;
