import React, { useState } from "react";
import API from "../services/api";

const CalorieEstimator = () => {
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);
  const [result, setResult] = useState(null);

  const handleEstimate = async () => {
    const formData = new FormData();
    if (image) formData.append("image", image);
    if (description) formData.append("description", description);

    const res = await API.post("/calorie/estimate", formData);
    setResult(res.data);
  };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Ước lượng calo từ ảnh hoặc mô tả</h2>
      <input
        type="file"
        onChange={(e) => setImage(e.target.files[0])}
        className="mb-3 block"
      />
      <textarea
        placeholder="Mô tả món ăn..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className="w-full mb-3 px-3 py-2 border rounded"
      />
      <button onClick={handleEstimate} className="bg-purple-600 text-white px-4 py-2 rounded">
        Ước lượng calo
      </button>
      {result && (
        <div className="mt-4 bg-gray-100 p-4 rounded">
          <p><strong>Món ăn:</strong> {result.food}</p>
          <p><strong>Calo ước lượng:</strong> {result.calories} cal</p>
        </div>
      )}
    </div>
  );
};

export default CalorieEstimator;
