import React, { useState } from 'react';
import { generateWorkoutPlan } from '../services/api';
import WorkoutCard from '../components/WorkoutCard';

const WorkoutPlan = () => {
  const [goalType, setGoalType] = useState('lose');
  const [amount, setAmount] = useState('');
  const [unit, setUnit] = useState('kg');
  const [workoutPlan, setWorkoutPlan] = useState([]);
  const [error, setError] = useState('');

  const handleGenerateWorkout = async () => {
    if (!amount) {
      setError('Vui l√≤ng nh·∫≠p s·ªë l∆∞·ª£ng m·ª•c ti√™u');
      return;
    }

    try {
      const res = await generateWorkoutPlan({
        userId: 1, // gi·∫£ ƒë·ªãnh test userId
        goalType,
        amount,
        unit,
      });
      setWorkoutPlan(res.data.workouts || []);
      setError('');
    } catch (err) {
      setError('ƒê√£ x·∫£y ra l·ªói khi g·ªçi API');
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6">
      <h2 className="text-3xl font-bold mb-6">üí™ L√™n k·∫ø ho·∫°ch t·∫≠p luy·ªán</h2>
      
      <div className="bg-white p-4 rounded-lg shadow-md space-y-4 mb-6">
        <div className="flex flex-col md:flex-row md:space-x-4">
          <select
            className="w-full border p-2 rounded"
            value={goalType}
            onChange={(e) => setGoalType(e.target.value)}
          >
            <option value="lose">Gi·∫£m c√¢n</option>
            <option value="gain">TƒÉng c√¢n</option>
            <option value="muscle">TƒÉng c∆°</option>
          </select>

          <input
            type="number"
            placeholder="S·ªë l∆∞·ª£ng (vd: 5)"
            className="w-full border p-2 rounded"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />

          <select
            className="w-full border p-2 rounded"
            value={unit}
            onChange={(e) => setUnit(e.target.value)}
          >
            <option value="kg">kg</option>
            <option value="%">%</option>
          </select>
        </div>

        {error && <div className="text-red-500">{error}</div>}

        <button
          onClick={handleGenerateWorkout}
          className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700 w-full md:w-auto"
        >
          T·∫°o k·∫ø ho·∫°ch t·∫≠p luy·ªán
        </button>
      </div>

      <div className="space-y-4">
        {workoutPlan.length > 0 ? (
          workoutPlan.map((workout, index) => (
            <WorkoutCard key={index} workout={workout} />
          ))
        ) : (
          <p className="text-gray-500">Ch∆∞a c√≥ k·∫ø ho·∫°ch n√†o ƒë∆∞·ª£c t·∫°o.</p>
        )}
      </div>
    </div>
  );
};

export default WorkoutPlan;
