import React, { useState } from 'react';
import MealCard from '../components/MealCard';
import axios from '../services/api';

const Home = () => {
  const [targetCalories, setTargetCalories] = useState('');
  const [mealsPerDay, setMealsPerDay] = useState('');
  const [generatedMeals, setGeneratedMeals] = useState([]);
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);
  const [estimatedCalories, setEstimatedCalories] = useState(null);

  const handleGenerateMeals = async () => {
    const res = await axios.post('/meals/generate', {
      targetCalories,
      mealsPerDay,
    });
    setGeneratedMeals(res.data.meals);
  };

  const handleEstimateCalories = async () => {
    if (image) {
      const formData = new FormData();
      formData.append('image', image);
      const res = await axios.post('/image/estimate', formData);
      setEstimatedCalories(res.data.calories);
    } else if (description) {
      const res = await axios.post('/image/estimate-text', { description });
      setEstimatedCalories(res.data.calories);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h2 className="text-3xl font-bold mb-6">🍽️ Tạo thực đơn theo nhu cầu</h2>

      <div className="bg-white p-4 rounded-lg shadow-md space-y-4 mb-6">
        <input
          type="number"
          placeholder="Nhập calo mục tiêu (vd: 2000)"
          value={targetCalories}
          onChange={(e) => setTargetCalories(e.target.value)}
          className="w-full border p-2 rounded"
        />
        <input
          type="number"
          placeholder="Số bữa ăn mỗi ngày (vd: 3)"
          value={mealsPerDay}
          onChange={(e) => setMealsPerDay(e.target.value)}
          className="w-full border p-2 rounded"
        />
        <button
          onClick={handleGenerateMeals}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Sinh thực đơn
        </button>
      </div>

      <div className="space-y-4">
        {generatedMeals.map((meal, index) => (
          <MealCard key={index} meal={meal} />
        ))}
      </div>

      <hr className="my-8" />

      <h2 className="text-3xl font-bold mb-6">📷 Ước lượng calo từ ảnh hoặc mô tả</h2>
      <div className="bg-white p-4 rounded-lg shadow-md space-y-4">
        <input
          type="file"
          onChange={(e) => setImage(e.target.files[0])}
          className="w-full"
        />
        <input
          type="text"
          placeholder="Hoặc mô tả món ăn (vd: 1 tô phở bò)"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="w-full border p-2 rounded"
        />
        <button
          onClick={handleEstimateCalories}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Ước lượng calo
        </button>
        {estimatedCalories && (
          <p className="text-lg font-semibold text-gray-700">
            👉 Calo ước tính: {estimatedCalories} kcal
          </p>
        )}
      </div>
    </div>
  );
};

export default Home;
