import React, { useState } from 'react';
import { generateWorkoutPlan } from '../services/api';
import WorkoutCard from '../components/WorkoutCard';

const WorkoutPlan = () => {
  const [workouts, setWorkouts] = useState([]);

  const handleGenerate = async () => {
    const res = await generateWorkoutPlan({ userId: 1 });
    setWorkouts(res.data.workouts);
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-2">Lên kế hoạch tập luyện</h2>
      <button onClick={handleGenerate} className="bg-green-600 text-white px-4 py-2 mb-4 rounded">Tạo từ AI</button>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
        {workouts.map((workout, idx) => <WorkoutCard key={idx} workout={workout} />)}
      </div>
    </div>
  );
};

export default WorkoutPlan;
