import React, { useState } from "react";

const MealGenerator = () => {
  const [meals, setMeals] = useState([]);

  const generateMeals = async () => {
    const res = await fetch("http://localhost:5000/api/meals/generate", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${JSON.parse(localStorage.getItem("user")).token}`,
      },
      body: JSON.stringify({ goal: "maintain" }),
    });
    const data = await res.json();
    setMeals(data.meals);
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Tạo thực đơn theo mục tiêu</h2>
      <button onClick={generateMeals} className="mb-4 bg-blue-600 text-white px-4 py-2 rounded">
        Tạo thực đơn
      </button>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {meals.map((meal, index) => (
          <div key={index} className="p-4 border rounded shadow">
            <img src={meal.image} alt={meal.name} className="w-full h-40 object-cover rounded mb-2" />
            <h3 className="text-lg font-semibold">{meal.name}</h3>
            <p>{meal.calories} cal</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MealGenerator;
